import React, { useEffect, useRef, useState } from "react";
import { Sparkles } from "lucide-react";

// Wails runtime event helpers (already generated by template)
import { EventsOn, EventsOff } from "../wailsjs/runtime/runtime.js";

// Prefer the generated bindings if they exist, otherwise fall back to window.go
// The generated file will update after running `wails dev`/`wails build`
let API = null;
try {
  // eslint-disable-next-line no-undef
  API = window?.go?.app?.App;
} catch (_) {
  API = null;
}

function Badge({ children, variant = "success" }) {
  const dot =
    variant === "success"
      ? "bg-emerald-400"
      : variant === "danger"
      ? "bg-rose-400"
      : "bg-slate-400";

  return (
    <span className="inline-flex items-center gap-1.5 px-2.5 py-1 rounded-full text-xs font-medium ring-1 ring-inset bg-neutral-900 text-slate-300 ring-neutral-800">
      <span className={`h-1.5 w-1.5 rounded-full ${dot}`} />
      {children}
    </span>
  );
}

const describeEndpoint = (p) => {
  if (!p) return "Proxied endpoint.";

  if (p == "/openai/v1/chat/completions") {
    return "OpenAI API";
  }

  if (p == "/bedrock/claude/v1/messages") {
    return "Anthropic Claude API";
  }

  if (p == "/provider/bedrock/format/openai/v1/chat/completions") {
    return "Anthropic Claude API translated to an OpenAI compatible API";
  }

  if (p == "/google/gemini/v1beta/models/{model}:generateContent") {
    return "Gemini API";
  }

  if (p == "/google/gemini/v1beta/models/{model}:streamGenerateContent") {
    return "Gemini streamed API";
  }

  return "Proxied endpoint.";
};

const getProviderColor = (provider) => {
  switch (provider) {
    case "OpenAI":
      return {
        container: "bg-emerald-950/60 border-emerald-800/80",
        divide: "divide-emerald-800/80",
      };
    case "Anthropic":
      return {
        container: "bg-amber-950/60 border-amber-800/80",
        divide: "divide-amber-800/80",
      };
    case "Google":
      return {
        container: "bg-sky-950/60 border-sky-800/80",
        divide: "divide-sky-800/80",
      };
    default:
      return {
        container: "bg-neutral-800 border-neutral-700",
        divide: "divide-neutral-700",
      };
  }
};

const getEndpointProvider = (path) => {
  if (path.startsWith("/openai")) return "OpenAI";
  if (path.startsWith("/bedrock")) return "Anthropic";
  if (path.startsWith("/provider/bedrock")) return "Anthropic";
  if (path.startsWith("/google")) return "Google";
  return "Other";
};

const groupEndpoints = (endpoints) => {
  if (!endpoints) return {};
  return endpoints.reduce((acc, endpoint) => {
    const provider = getEndpointProvider(endpoint.in);
    if (!acc[provider]) {
      acc[provider] = [];
    }
    acc[provider].push(endpoint);
    return acc;
  }, {});
};

export default function App() {
  const [port, setPort] = useState(null);
  const [running, setRunning] = useState(false);
  const [logs, setLogs] = useState("");
  const [busy, setBusy] = useState(false);
  const [endpoints, setEndpoints] = useState([]);
  const [activeTab, setActiveTab] = useState("routes");

  // Dark theme only
  const logsRef = useRef(null);

  // Auto-scroll logs
  useEffect(() => {
    if (!logsRef.current) return;
    logsRef.current.scrollTop = logsRef.current.scrollHeight;
  }, [logs]);

  // Initialisation: fetch defaults & status
  useEffect(() => {
    let mounted = true;

    (async () => {
      try {
        if (API?.IsRunning) {
          const s = await API.IsRunning();
          if (mounted) setRunning(!!s);
        }
        if (API?.GetLogs) {
          const l = await API.GetLogs();
          if (mounted && l) setLogs(l);
        }
        if (API?.GetEndpoints) {
          const info = await API.GetEndpoints();
          if (mounted && info) {
            if (info.endpoints) setEndpoints(info.endpoints);
          }
        }
        if (API?.GetPort) {
          const p = await API.GetPort();
          if (mounted) setPort(p);
        }
      } catch (e) {
        // ignore
      }
    })();

    // Subscribe to runtime events
    const offA = EventsOn("proxy:status", (status) => {
      setRunning(status === "running");
    });
    const offB = EventsOn("proxy:log", (line) => {
      setLogs((prev) => prev + (prev.endsWith("\n") || prev.length === 0 ? "" : "\n") + line);
    });
    const offC = EventsOn("proxy:log:cleared", () => setLogs(""));

    return () => {
      mounted = false;
      try {
        EventsOff("proxy:status", offA);
        EventsOff("proxy:log", offB);
        EventsOff("proxy:log:cleared", offC);
      } catch (_) {
        // ignore
      }
    };
  }, []);


  const onStart = async () => {
    if (!API?.StartProxy) return;
    setBusy(true);
    try {
      await API.StartProxy();
    } catch (e) {
      console.error(e);
    } finally {
      setBusy(false);
    }
  };

  const onStop = async () => {
    if (!API?.StopProxy) return;
    setBusy(true);
    try {
      await API.StopProxy();
    } catch (e) {
      console.error(e);
    } finally {
      setBusy(false);
    }
  };

  const onClearLogs = async () => {
    if (!API?.ClearLogs) {
      setLogs("");
      return;
    }
    await API.ClearLogs();
  };



  return (
    <div className="h-screen overflow-hidden select-none bg-slate-900 text-slate-100 relative flex flex-col">
      <div className="fixed top-0 left-0 right-0 h-2 app-drag z-10" />
      <header className="px-3 py-3 border-b border-neutral-700 bg-neutral-900/60 backdrop-blur">
        <div className="relative w-full flex items-center justify-between app-no-drag">
          <div className="absolute left-1/2 -translate-x-1/2">
            <div className="relative inline-flex rounded-md border border-neutral-700 bg-neutral-800/60 p-0.5 overflow-hidden">
              <span
                className={`pointer-events-none absolute top-0.5 left-0.5 bottom-0.5 w-24 rounded bg-neutral-700 transform-gpu transition-transform duration-100 ease-in-out ${activeTab === "logs" ? "translate-x-24" : "translate-x-0"}`}
              />
              <button
                onClick={() => setActiveTab("routes")}
                className={`relative z-10 w-24 text-center px-3 py-1.5 text-sm rounded ${activeTab === "routes" ? "text-slate-100" : "text-slate-300 hover:text-slate-100"}`}
              >
                Routes
              </button>
              <button
                onClick={() => setActiveTab("logs")}
                className={`relative z-10 w-24 text-center px-3 py-1.5 text-sm rounded ${activeTab === "logs" ? "text-slate-100" : "text-slate-300 hover:text-slate-100"}`}
              >
                Logs
              </button>
            </div>
          </div>
          <div className="flex items-center gap-3">
            <div className="h-9 w-9 rounded-lg bg-neutral-800/60 ring-1 ring-neutral-700 grid place-items-center">
              <Sparkles className="h-5 w-5 text-slate-300" />
            </div>
            <div>
              <h1 className="text-lg font-semibold tracking-tight text-slate-200">
                Proximity
              </h1>
              <p className="text-xs text-slate-400">
                Enterprise AI APIs via AI-Gateway
              </p>
            </div>
          </div>
          <div className="flex items-center gap-2">
            {port != null && (
              <span className="inline-flex items-center gap-1.5 px-2.5 py-1 rounded-full text-xs font-medium ring-1 ring-inset bg-neutral-900 text-slate-300 ring-neutral-800 select-text cursor-text">{`Listening on port ${port}`}</span>
            )}
            {!running ? (
              <button
                disabled={busy}
                onClick={onStart}
                className="inline-flex items-center justify-center w-28 rounded-md bg-[#0a84ff] hover:bg-[#409cff] text-white font-medium px-3 py-1.5 text-sm disabled:opacity-50 disabled:cursor-not-allowed transition"
              >
                Start Proxy
              </button>
            ) : (
              <button
                disabled={busy}
                onClick={onStop}
                className="inline-flex items-center justify-center w-28 rounded-md bg-[#ff453a] hover:bg-[#ff6961] text-white font-medium px-3 py-1.5 text-sm disabled:opacity-50 disabled:cursor-not-allowed transition"
              >
                Stop Proxy
              </button>
            )}
          </div>
        </div>
      </header>

      <main className="p-3 flex-1 overflow-hidden min-h-0 min-w-0">
        <div className="rounded-lg border border-neutral-700 bg-neutral-800/60 p-0 divide-y divide-neutral-700 h-full min-h-0 min-w-0 w-full flex flex-col overflow-hidden">
          <div className="flex items-center justify-between p-3 h-12 bg-neutral-800 shrink-0">
            <div className="text-sm font-semibold text-slate-300">{activeTab === "logs" ? "Logs" : "Routes"}</div>
            {activeTab === "logs" && (
              <button
                onClick={onClearLogs}
                className="inline-flex items-center rounded-md border border-neutral-700 bg-neutral-800 hover:bg-neutral-700 px-3 py-1.5 text-sm text-slate-200 transition"
              >
                Clear logs
              </button>
            )}
          </div>
          <div ref={logsRef} className={`flex-1 min-h-0 min-w-0 overflow-auto p-3 ${activeTab === "logs" ? "bg-neutral-900" : ""}`}>
            {activeTab === "logs" ? (
              <div className="font-mono text-xs leading-relaxed text-slate-200/90 whitespace-pre-wrap break-all w-full min-w-0 min-h-0 select-text">
                {logs?.length ? logs : "No logs yet."}
              </div>
            ) : (
              <div>
                {endpoints?.length ? (
                  <div className="space-y-4">
                    {Object.entries(groupEndpoints(endpoints)).map(
                      ([provider, providerEndpoints]) => {
                        const colors = getProviderColor(provider);
                        return (
                          <div
                            key={provider}
                            className={`rounded-lg border ${colors.container}`}
                          >
                          <div
                            className={`p-3 border-b ${
                              colors.container.split(" ")[1]
                            } bg-white/5`}
                          >
                            <h2 className="text-base font-semibold text-slate-200">
                              {provider}
                            </h2>
                          </div>
                            <div className={`divide-y ${colors.divide}`}>
                              {providerEndpoints.map((e, i) => (
                                <div
                                  key={i}
                                  className="p-3 hover:bg-white/5"
                                >
                                  <div className="text-[15px] font-medium text-slate-200">
                                    {describeEndpoint(e.in)}
                                  </div>
                                  <div className="mt-1.5">
                                    <span className="inline-block font-mono text-sm text-slate-100 bg-black/20 ring-1 ring-white/10 rounded px-3 py-1.5 select-text">
                                      {e.in}
                                    </span>
                                  </div>
                                </div>
                              ))}
                            </div>
                          </div>
                        );
                      }
                    )}
                  </div>
                ) : (
                  <div className="text-sm text-slate-500">
                    No endpoints configured.
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </main>

    </div>
  );
}
